import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Meteor } from 'meteor/meteor';
import { withTracker } from 'meteor/react-meteor-data';

import { Nendoroids } from '../api/nendoroids.js';

import Nendoroid from './Nendoroid.js';
import AccountsUIWrapper from './AccountsUIWrapper.js';

// App component - represents the whole app
class App extends Component {

    constructor(props) {
        super(props);

        this.state = {
            hideObtained: false,
        };
    }

    handleSubmit(event) {
        event.preventDefault();

        // Find the text field via the React ref
        const name = ReactDOM.findDOMNode(this.refs.nameInput).value.trim();

        Nendoroids.insert({
            name,
            createdAt: new Date(),              // current time
            owner: Meteor.userId(),             // _id of logged in user
            username: Meteor.user().username,   // username of logged in user
        });

        // Clear form
        ReactDOM.findDOMNode(this.refs.nameInput).value = '';
    }

    toggleHideObtained() {
        this.setState({
            hideObtained: !this.state.hideObtained,
        });
    }

    renderNendoroids() {
        let filteredNendoroids = this.props.nendoroids;
        if (this.state.hideObtained) {
            filteredNendoroids = filteredNendoroids.filter(nendoroid => !nendoroid.checked);
        }
        return filteredNendoroids.map(nendoroid => (
            <Nendoroid key={nendoroid._id} nendoroid={nendoroid} />
        ));
    }

    render() {
        return (
            <div className="container">
                <header>
                    <h1>Nendoroid List ({this.props.count})</h1>

                    <label className="hide-completed">
                        <input type="checkbox" readOnly
                            checked={this.state.hideObtained}
                            onClick={this.toggleHideObtained.bind(this)}
                        />
                        Hide Obtained Nendoroids
                    </label>

                    <AccountsUIWrapper />

                    {this.props.currentUser ?
                        <form className="new-task" onSubmit={this.handleSubmit.bind(this)} >
                            <input type="text" ref="nameInput" placeholder="Product Name" />
                        </form> : ''
                    }
                </header>

                <ul className="nendoroidList">
                    {this.renderNendoroids()}
                </ul>
            </div>
        );
    }
}

export default withTracker(() => {
    return {
        nendoroids: Nendoroids.find({}, { sort: { createdAt: -1 } }).fetch(),
        count: Nendoroids.find({ checked: { $ne: true } }).count(),
        currentUser: Meteor.user(),
    };
})(App);
